package com.example.sqllite;

import androidx.appcompat.app.AppCompatActivity;
import androidx.room.Room;
import android.content.Intent;
import android.os.Bundle;
import android.util.Log;
import android.view.View;
import android.widget.GridView;
import com.example.sqllite.DAO.StoryDao;
import com.example.sqllite.Models.Story;
import com.example.sqllite.adapter.StoryAdapter;

import java.util.ArrayList;
import java.util.List;

public class StoryActivity extends AppCompatActivity {

    private AppDatabase db;
    private StoryAdapter adapter;

    @Override
    protected void onCreate(Bundle savedInstanceState) {
        super.onCreate(savedInstanceState);
        setContentView(R.layout.liststory_view);

        db = Room.databaseBuilder(getApplicationContext(),
                AppDatabase.class, "database-name-v2").build();

        GridView gridView = findViewById(R.id.gridView);
        adapter = new StoryAdapter(this, new ArrayList<>());
        gridView.setAdapter(adapter);
        // Thêm dữ liệu mẫu nếu cơ sở dữ liệu trống
        // Thêm dữ liệu mẫu nếu cơ sở dữ liệu trống
        new Thread(new Runnable() {
            @Override
            public void run() {
                StoryDao storyDao = db.storyDao();
                if (storyDao.getAllStories().isEmpty()) {
                    storyDao.insert(
                            new Story("data:image/jpeg;base64,","Doremon", " Fujiko F. Fujio", "Genre 3", "Đôrêmon là một bộ truyện tranh Nhật Bản nổi tiếng với câu chuyện xoay quanh chú mèo máy tương lai Đôrêmon và cậu bé Nobita. Bộ truyện này được sáng tác bởi Fujiko F. Fujio."),
                            new Story("data:image/jpeg;base64,","", "Author 3", "Genre 3", "Conan"),
                            new Story("https://cdn.chanhtuoi.com/uploads/2022/01/truyen-co-tich-1.jpg", "","Author 3", "Genre 3", "So Dua"),

                            new Story("data:image/jpeg;base64,","", "Author 3", "Genre 3", "So Dua"),
                            new Story("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcS5lRWFq6ULdjJKoMK4aFuAOrco7JxojDymFA&s", "","Author 3", "Genre 3", "Romeo and Juliet"),
                            new Story("data:image/jpeg;base64,", "","Author 3", "Genre 3", "Conan"),
                            new Story("https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQFx2nbupPmHvhXL4jCrEzyhGrfmr52piic2g&s", "","Author 3", "Genre 3", "Love Story"),

                    new Story("data:image/jpeg;base64,", "","Author 3", "Genre 3", "Gone with the Wind"),

                    new Story("data:image/jpeg;base64,", "","Author 3", "Genre 3", "Truyện Ngộn Tìn")


                    );
                }
                retrieveAndDisplayStories();
            }
        }).start();
    }

    private void retrieveAndDisplayStories() {
        new Thread(new Runnable() {
            @Override
            public void run() {
                StoryDao storyDao = db.storyDao();
                List<Story> stories = storyDao.getAllStories();
                Log.d("StoryActivity", "Number of stories loaded: " + stories.size());
                runOnUiThread(new Runnable() {
                    @Override
                    public void run() {
                        adapter.setStories(stories);
                    }
                });
            }
        }).start();
    }

}
